import React, { useState, useRef } from 'react';
import './Topbar.css'
import styled from "@emotion/styled";
import Fade from 'react-reveal/Fade';
import { disableBodyScroll, clearAllBodyScrollLocks } from 'body-scroll-lock';

import SideBar from "./Sidebar";



const Topbar = () => {

    const [showMenu, setShowMenu] = useState(false);

    const topbarRef = useRef()

    const onOpen = () => {
        const targetElement = document.querySelector(".app");
        disableBodyScroll(targetElement);
        setShowMenu(true);
    };

    const onClose = () => {
        setShowMenu(false);
        clearAllBodyScrollLocks();
    };

    const socialMedia = [
        {
            "icon": "far fa-envelope",
            "title": "Contact us through Email",
            "url": "mailto:mail@shakticon.com"
        },
        {
            "icon": "fab fa-instagram",
            "title": "Follow our Instagram handle",
            "url": "https://instagram.com/shakticon"
        },
        {
            "icon": "fab fa-twitter",
            "title": "Follow us on Twitter",
            "url": "https://twitter.com/shakticon"
        },
        {
            "icon": "fa fa-flag",
            "title": "View our CTFTime ranking",
            "url": "https://ctftime.org/team/61083"
        },
        {
            "icon": "fab fa-discord",
            "title": "Join Discord Server",
            "url": "https://discord.gg/Dv8hEV7vWv"
        },
        {
            "icon": "fab fa-github",
            "title": "View our GitHub",
            "url": "https://github.com/Team-Shakti"
        }
    ];

    return (<React.Fragment>
        <div className="topbar" ref={topbarRef}>
            <div className="row w-100 mx-0">
                <div className="col-4 col-md-3 d-flex justify-content-md-center px-3">
                    <a href="/">
                        <img className="image" height="30px" draggable="false" width="auto" src={require('../../images/logomain.png').default} alt="Team Shakti" />
                    </a>
                </div>
                <div className="col-8 d-none d-md-flex justify-content-md-end align-items-center">
                    <nav>
                        <Fade delay={0}>
                            <a className="newone" href="/about">About</a>
                        </Fade>
                        <Fade delay={200}>
                            <a className="newone" href="/trainings">speaker</a>
                        </Fade>
                        <Fade delay={450}>
                            <a className="newone" href="/speakers">Sponsors</a>
                        </Fade>
                        <Fade delay={550}>
                            <a className="newone" href="/ctf">Promote</a>
                        </Fade>
                    </nav>
                </div>
                <div className="col-8 d-flex d-md-none align-items-center justify-content-end px-2">
                    <button onClick={onOpen}>
                    <i class="fa fa-bars" aria-hidden="true"></i>
                    </button>
                </div>
                {/*<div className="col-3 d-none d-md-flex align-items-center">
                    {/*<Fade left>*/}
                    {/*    <a*/}
                    {/*       className="register-button" target="_blank" rel="noreferrer nofollow"*/}
                    {/*       href="https://docs.google.com/forms/d/e/1FAIpQLSeiITkqpmhPRHWQspiLt27hDV2nFlwW9QoyzyFZVjM5YmSqwg/viewform"*/}
                    {/*    >*/}
                    {/*        Register*/}
                    {/*    </a>*/}
                    {/*</Fade>*/}
                    {/*socialMedia.map((s, index) =>
                        <Fade right delay={index*200}>
                            <a
                                href={s.url}
                                target="_blank" rel="nofollow noreferrer"
                                className="social-icon"
                                title={s.title}
                                aria-label={s.title}
                            >
                                <i className={s.icon} aria-hidden="true" />
                            </a>
                        </Fade>
                    )}
                    </div>*/}
            </div>
        </div>
        {showMenu && <SideBar  onClose={onClose} />}
    </React.Fragment>);
};

export default Topbar;
